import { FETCH_WEATHER, REMOVE_ITEM } from "../actions";
export default function(state=[], action) {
    //console.log('Action receved', action);
    switch (action.type) {
        case FETCH_WEATHER:
            //return state.concat([action.payload.data]);
            return [action.payload.data, ...state];
        case REMOVE_ITEM:
            return [state.weather.filter(cityData => cityData.city.id !== action.payload) ];
    }
    return state;
}
